import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { BrowserRouter as Router, Route, Switch, Redirect, useHistory } from 'react-router-dom'
import $ from 'jquery';
import 'bootstrap/dist/css/bootstrap.css';


import SignForm from '../containers/SignForm.js'
import Header from '../containers/Header.js';
import Footer from '../containers/Footer.js';
function App(props) {
  const { proxy, checkAuth, theme, /* refreshTokens, */ userRole } = props;
  const history = useHistory();
  useEffect(() => {
    function logIn(path, token, refreshPath, refreshToken) {
      checkAuth(path, token, refreshPath, refreshToken);
    }
    if (localStorage.getItem('token')) {
      logIn(proxy + '/me', 'ssdfsdf', proxy + '/refresh', 'sgdsfgsdf');
    }
  }, [checkAuth, proxy]);

  useEffect(() => {
    document.querySelector('body').className = theme;
  }, [theme]);

  useEffect(() => {
    if (userRole === 'user') {
      history.push('/me');
    }
  }, [userRole])
  return (
    <Router>
      <div className='content'>
        <Header />
        <Switch>
          <Route exact path='/'>
            <SignForm />
          </Route>
          <PrivateRoute exact path='/me'>
            <div>sdf sdf sdf sdf</div>
          </PrivateRoute>
        </Switch>
      </div>
      <Footer />
    </Router>
  )
}
App.propTypes = {
  proxy: PropTypes.string.isRequired,
  checkAuth: PropTypes.func.isRequired,
  theme: PropTypes.string.isRequired,
  userRole: PropTypes.string.isRequired
}
function PrivateRoute({ children, userRole, ...rest }) {
  return (
    <Route
      {...rest}
      render={({ location }) =>
        userRole === 'guest' ? (
          children
        ) : (
            <Redirect
              to={{
                pathname: "/login",
                state: { from: location }
              }}
            />
          )
      }
    />
  );
}
export default App;
