import {REQUEST_TOKEN,RECEIVE_USER_DATA} from '../actions/tokenManagement';
import {REQUEST_SIGN_FORM,RESEIVE_SIGN_FORM} from '../actions/'

function user(state = {
    isFetching: false,
    data: {}
}, action) {
    switch (action.type) {
        case REQUEST_TOKEN:
            return {...state,...{isFetching:true}};
        case RECEIVE_USER_DATA:
            return {
                ...state,
                ...{
                    isFetching:false,
                    data: action.userData
                }
            }
        default:
            return state;
    }
}
export default user;
