import { connect } from 'react-redux'
import { logInWithToken } from '../actions/tokenManagement';
import App from '../components/App.jsx';


function mapStateToProps(state) {
    const { proxy,theme,language } = state
    return {
        proxy,
        theme,
        language
    }
}
function mapDispatchToProps(dispatch){ 
    return{
        checkAuth: (path,token,refreshPath,refreshToken)=>dispatch(logInWithToken(path,token,refreshPath,refreshToken)),
         refreshTokens: (path,refreshToken)=>dispatch(refreshTokens(path,refreshToken)) 
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(App);
