import { REQUEST_SIGN_FORM, RESEIVE_SIGN_FORM,USER_WAS_CREATED,USER_LOGGED_IN } from '../actions/signFormControllerActions';
function signForm(state = {
    isFetching: false,
    status: ''
}, action) {
    switch (action.type) {
        case REQUEST_SIGN_FORM:
            return { ...state, ...{ isFetching: true } }
        case RESEIVE_SIGN_FORM:
            return { ...state, ...{ isFetching: false } }
        case USER_WAS_CREATED:
            return { ...state, ...{ status: USER_WAS_CREATED} }

        default:
            return state
    }
}
export default signForm;
