import { connect } from 'react-redux'
import { logInWithToken,refreshTokens } from '../actions/tokenManagement';
import App from '../components/App.jsx';


function mapStateToProps(state) {
    console.log(state);
    const { proxy,theme,language } = state
    return {
        proxy,
        theme,
        language
    }
}
function mapDispatchToProps(dispatch){ 
    return{
        checkAuth: (path,token,refreshTokenFn)=>dispatch(logInWithToken(path,token,refreshTokenFn)),
        refreshTokens: (path,refreshToken)=>dispatch(refreshTokens(path,refreshToken))
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(App);
