import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import $ from 'jquery';
import 'bootstrap/dist/css/bootstrap.css';


import SignForm from '../containers/SignForm.js'
import Header from '../containers/Header.js';
import Footer from '../containers/Footer.js';
function App(props) {
  const { proxy, checkAuth, theme, refreshTokens } = props;
  useEffect(() => {
    function logIn(path, token, refreshPath, refreshToken) {
      checkAuth(path, token, refreshPath, refreshToken);
    }
    if (!localStorage.getItem('token')) {
      logIn(proxy + '/me', 'ssdfsdf', proxy + '/refresh', 'sgdsfgsdf');
    }
  }, [checkAuth, proxy]);

  useEffect(() => {
    document.querySelector('body').className = theme;
  }, [theme]);


  return (
    <Router>
      <div className='content'>
        <Header />
        <Switch>
          <Route exact path='/'>
            <SignForm />
          </Route>
          <Route exact path='/me'>
            <SignForm />
          </Route>
        </Switch>
      </div>
      <Footer />
    </Router>
  )
}
App.propTypes = {
  proxy: PropTypes.string.isRequired,
  checkAuth: PropTypes.func.isRequired,
  theme: PropTypes.string.isRequired
}
export default App;
