import React, { useRef, useEffect } from 'react';
import Switcher from './Switcher';
import '../style/header.css';
import {DARK,LIGHT} from '../actions/themeActions';

const Header = (props) => {
    const { toggleTheme, theme } = props;
    const changeLangElement = useRef(null);
    const changeThemeElement = useRef(null);
    function toggleCheckedLang() {
        changeLangElement.current.checked = !changeLangElement.current.checked;
    }
    function toggleCheckedTheme() {
        changeThemeElement.current.checked = !changeThemeElement.current.checked;
    }
    useEffect(() => {
        if(theme === DARK)
            toggleCheckedTheme();
    }, []);
    return (
        <div className='container-fluid header-wrapper'>
            <header className='container header'>
                <div className='language-switcher header-switcher d-flex align-items-center' onClick={toggleCheckedLang}>
                    <label htmlFor='languageSwitcher' className="mb-0 mr-1 header-switcher-label">Ua</label>
                    <Switcher refElement={changeLangElement} />
                    <label htmlFor='languageSwitcher' className="mb-0 ml-1 header-switcher-label">En</label>
                </div>
                <div className='theme-switcher  header-switcher d-flex align-items-center' onClick={toggleCheckedTheme}>
                    <label htmlFor='themeSwitcher' className="mb-0 mr-1 header-switcher-label">Light</label>
                    <Switcher refElement={changeThemeElement} />
                    <label htmlFor='themeSwitcher' className="mb-0 ml-1 header-switcher-label">Dark</label>
                </div>
            </header>
        </div>
    )
}
export default Header;
