import React from 'react';
import {ENGLISH} from '../../actions/languageActions';
const SignIn = ({language}) => {
    function signIn(e) {
        e.preventDefault();
        console.log(4);
    }
    return (
        <form onSubmit={signIn} className="form sign-in">
            <h2>{language === ENGLISH ? 'Welcome back' : 'З поверненням'}</h2>
            <label>
                <span>{language === ENGLISH ? 'Email' : 'Емейл'}</span>
                <input type="email" />
            </label>
            <label>
                <span>{language === ENGLISH ? 'Password' : 'Пароль'}</span>
                <input type="password" />
            </label>
            <button on type="button" className="submit">{language === ENGLISH ? 'Sign In' : 'Автентифікація'}</button>
        </form>
    )
}
export default SignIn;
