import React, { useRef } from 'react';
import Switcher from './Switcher';
import '../style/header.css'
const Header = (props) => {
    const changeLangElement = useRef(null);
    function toggleCheckedLang() {
        changeLangElement.current.checked = !changeLangElement.current.checked;
        console.log(changeLangElement.current.checked);
    }
    return (
        <div className='container-fluid header-wrapper'>
            <header className='container header'>
                <div className='language-switcher mr-1 nav-item d-flex align-items-center' onClick={toggleCheckedLang}>
                    <label htmlFor='languageSwitcher' className="mb-0 mr-1">Ua</label>
                    <Switcher refElement={changeLangElement} />
                    <label htmlFor='languageSwitcher' className="mb-0 ml-1">En</label>
                </div>
            </header>
        </div>
    )
}
export default Header;
