import {ACCESS_TOKEN_WAS_NOT_VALID,REFRESH_TOKEN_WAS_NOT_VALID} from '../actions/tokenManagement';
function token(state = {
    accessToken: localStorage.getItem('accessToken') || 'null',
    refreshToken: localStorage.getItem('refreshToken') || 'null'
},action) {
    console.log(action.type);
    switch (action.type) {
        case ACCESS_TOKEN_WAS_NOT_VALID:
            return {...state,...{accessToken:null}}
        case REFRESH_TOKEN_WAS_NOT_VALID:
            console.log(3434);
            return {...state,...{accessToken:null,refreshToken:null}}
        default:
            return state;
    }
}
export default token;
